        // console.log(removeItem)
    }
    return(
        <div>
            {
                tour.map( (tourInfo) => {
                    const {id , name, info, image,price} = tourInfo
                    return(
                        <div key={id} className="card">
                            <img src={image} alt="image" width="100%"/>
                            <div>
                                <p><b>{name}</b></p>
                                <p>{price}</p>
                            </div>
                            <p>
                                {readmore ? info : `${info.substring(0, 120)}...`}
                                <button onClick={()=> setReadmore(!readmore)}>{ readmore ? "Read More" : "Read Less"}</button>
                            </p>
                            <button id={id} onClick={removeCard}>Not Interested</button>
                        </div>

                    )
                })
            }

        </div>
    )
}

export default Card
