import React, { useState } from "react";
import data from "./data";
const Question = () => {
    const [question, setQuestion] = useState(false)

    console.log(question)
    return(
       <>
        <h2>Single Question</h2> 
       {
        data.map((questionList) => {
          const {id, title, text} = questionList

          return(

            <div key={data.id}>
                <div>
                    <h2>{title}</h2>
                    <button  onClick={() => setQuestion(!question)}>clk</button>
                </div>
                {question && <p>{text}</p>}
            </div>
          )
        })
       }
       </>
    )
}

export default Question
